import * as React from 'react';
import { connect } from 'react-redux';
import { RouteComponentProps } from 'react-router';
import { ApplicationState } from '../store';

//import * as CounterStore from '../store/Counter';
import * as TarjetaStore from '../store/TarjetaCredito';
import {Producto} from '../types'
import { TarjetaCredito } from '../models/tarjetacredito'
type ProductProps =
TarjetaStore.WeatherTarjetaState &
    typeof TarjetaStore.actionCreators &
    RouteComponentProps<{}>;

class TarjetaCreditos extends React.PureComponent<ProductProps, Producto> {

    constructor(props: any) {
        super(props);
        
        this.state = {
            id: '1',
            name: 'Product 1',
            description:'Description product 1',
            price: "20.00"
        }
        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.handleChange3 = this.handleChange3.bind(this);
        this.handleChange4 = this.handleChange4.bind(this);
        this.añadirProducto = this.añadirProducto.bind(this);
        this.editarProducto = this.editarProducto.bind(this);
    }

    public componentDidMount() {
        this.ensureDataFetched();
    }

    // This method is called when the route parameters change
    public componentDidUpdate() {
        this.ensureDataFetched();
    }

    private ensureDataFetched() {
        const startDateIndex = parseInt(this.props.match.params.startDateIndex, 10) || 0;
        this.props.requestWeatherForecasts(startDateIndex);
    }

    editarProducto(forecast:any){
        this.setState({
            id: forecast.id,
            name: forecast.name,
            description:forecast.description,
            price: forecast.price
        });
    }
    añadirProducto(){
        var product:Producto; 
        product = {
            id: this.state.id,
            name: this.state.name,
            description:this.state.description,
            price: this.state.price
        }
        //this.props.addToCart(product);
    }
    handleChange1(e:any) {
        /* var obj[e.target.name] = e.target.value
        this.setState(obj); */
        //console.log(e.target.value);
        this.setState({ id: String(e.target.value) });
        //this.state.id = e.target.value;
    }
    handleChange2(e:any) {
        /* var obj[e.target.name] = e.target.value
        this.setState(obj); */
        //console.log(e.target.value);
        this.setState({ name: String(e.target.value) });
        //this.state.id = e.target.value;
    }
    handleChange3(e:any) {
        /* var obj[e.target.name] = e.target.value
        this.setState(obj); */
        //console.log(e.target.value);
        this.setState({ description: String(e.target.value) });
        //this.state.id = e.target.value;
    }
    handleChange4(e:any) {
        /* var obj[e.target.name] = e.target.value
        this.setState(obj); */
        //console.log(e.target.value);
        this.setState({ price: String(e.target.value) });
        //this.state.id = e.target.value;
    }
    public render() {

        return (
            <React.Fragment>
                <h1>
                    Ejemplo de CRUD de tarjetas de credito
                </h1>
                <form>
                    <div className="form-group">
                        <label>Código producto:</label>
                        <input 
                            onChange={this.handleChange1}
                            value={this.state.id} 
                            type="number" 
                            id="Iid" 
                            name="id" 
                            placeholder="Ingrese código" 
                            className="form-control" />
                    
                    </div>
                    <div className="form-group">
                        <label>Nombre:</label>
                        <input 
                            onChange={this.handleChange2}
                            value={this.state.name} 
                            type="text" 
                            id="Inombre" 
                            name="nombre" 
                            placeholder="Ingrese nombre" 
                            className="form-control" />
                    
                    </div>
                    <div className="form-group">
                        <label>Descripcion:</label>
                        <input 
                            onChange={this.handleChange3}
                            value={this.state.description}
                            type="text" 
                            id="Idescripcion" 
                            name="descripcion" 
                            placeholder="Ingrese descripcion" 
                            className="form-control" />
                    
                    </div>
                    <div className="form-group">
                        <label>Precio:</label>
                        <input 
                            onChange={this.handleChange4}
                            value={this.state.price}
                            title="Ingrese precio" 
                            type="number" 
                            id="Idescripcion" 
                            name="descripcion" 
                            placeholder="Ingrese precio" 
                            className="form-control" />
                    
                    </div>
                    <div className="btn-group" role="group" aria-label="Basic example">
                        <button type="button"
                            className="btn btn-primary btn-lg"
                            onClick={() => { this.añadirProducto(); }}>
                            addToCard
                        </button>
                        <button type="button"
                            className="btn btn-success">
                            Editar
                        </button>
                        <button type="reset"
                            className="btn btn-warning btn-lg">
                            Reset
                        </button>
                        <button type="button"
                            className="btn btn-danger btn-lg">
                            Salir
                        </button>
                    </div>
                </form>
                <table className='table table-striped' aria-labelledby="tabelLabel">
                <thead>
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Descripcion</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {this.props.tarjetas.map((forecast: TarjetaCredito) =>
                    <tr key={forecast.id}>
                        <td>{forecast.id}</td>
                        <td>{forecast.titular}</td>
                        <td>{forecast.numeroTarjeta}</td>
                        <td>{forecast.fechaExpiracion}</td>
                        <td>{forecast.cvv}</td>
                       {/*  <td>
                            <div className="btn-group" role="group" aria-label="Basic example">
                                <button 
                                    onClick={() => { this.editarProducto(forecast) }}
                                    type="button"
                                    className="btn btn-success">
                                    Editar
                                </button>
                                <button type="button"
                                    onClick={() => { this.props.deleteProduct(forecast.id); }}
                                    className="btn btn-danger">
                                    Eliminar
                                </button>
                            </div>
                        </td> */}
                    </tr>
                )} 
                {/* {<p aria-live="polite">Current products: <strong>{JSON.stringify(this.props.products)}</strong></p> } */}
                </tbody>
                </table>
            </React.Fragment>
        );
    }
};

export default connect(
    (state: ApplicationState) => state.tarjetas,
    TarjetaStore.actionCreators
)(TarjetaCreditos as any);
