import * as React from 'react';
import { connect } from 'react-redux';
import { RouteComponentProps } from 'react-router';
import { ApplicationState } from '../store';

//import * as CounterStore from '../store/Counter';
import * as ProductStore from '../store/Product';
import {Producto} from '../types'
import { TarjetaCredito } from '../models/tarjetacredito'

type ProductProps =
ProductStore.ProductState &
    typeof ProductStore.actionCreators &
    RouteComponentProps<{}>;


//Producto colocarlo en el extends para que funcione todo
class Product extends React.PureComponent<ProductProps, TarjetaCredito> {

    constructor(props: any) {
        super(props);
        
        this.state = {
            id: '1',
            name: 'Product 1',
            description:'Description product 1',
            price: "20.00"
        }
    } 
    public render() {

        return (
            <React.Fragment>
                <h1>
                    Ejemplo de tarjetas de credito
                </h1>
                <table className='table table-striped' aria-labelledby="tabelLabel">
                <thead>
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Descripcion</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {this.props.products.map((forecast: Producto) =>
                    <tr key={forecast.id}>
                        <td>{forecast.id}</td>
                        <td>{forecast.name}</td>
                        <td>{forecast.description}</td>
                        <td>
                            <div className="btn-group" role="group" aria-label="Basic example">
                                <button 
                                    onClick={() => { this.editarProducto(forecast) }}
                                    type="button"
                                    className="btn btn-success">
                                    Editar
                                </button>
                                <button type="button"
                                    onClick={() => { this.props.deleteProduct(forecast.id); }}
                                    className="btn btn-danger">
                                    Eliminar
                                </button>
                            </div>
                        </td>
                    </tr>
                )} 
                {/* {<p aria-live="polite">Current products: <strong>{JSON.stringify(this.props.products)}</strong></p> } */}
                </tbody>
                </table>
            </React.Fragment>
        );
    }
};

export default connect(
    (state: ApplicationState) => state.products,
    ProductStore.actionCreators
)(Product as any);
